        private string getRandomTrytes()
        {
            string trytes = "";
            Random rand = new Random();
            int i, r;
            for (i = 0; i < TRYTE_LENGTH; i++)
            {
                r = rand.Next(27);
                trytes += Constants.TryteAlphabet[r];
            }
            return trytes;
        }

        private bool isAllNines(string hash)
        {
            int i;
            for (i = 0; i < hash.Length; i++)
            {
                if (hash[i] != '9')
                {
                    return false;
                }
            }
            return true;
        }
